import pandas as pd
def search(location, non, q):   return (pd.read_csv('cities.csv') if location == 'город' else (pd.read_csv('countries.csv') if location == 'страна' else (pd.read_csv('regions.csv') if location == 'регион' else -1))).query(non + q)
print(search(input('город, страна, регион: ').lower(), input('название или номер: ').lower().replace('номер', 'ID').replace('название', 'Name'), input('Условие: ').lower().replace('=', '==').replace('равно', '==').replace('больше', '>').replace('>или==', '>=').replace('меньше', '<').replace('<или==', '<=').replace(' ', '').replace('*', '.*')))